    public static string GetDefaultCellsText(int leaf, int cell) => CellsTexts[$"{leaf}{cell}"];
    public static string GetUniversalText(int cell) => UniversalTexts[cell];

    public static string GetMoveText(int leaf, int cell)
    {
        if (UniversalTexts.ContainsKey(cell))
            return UniversalTexts[cell];
        return CellsTexts[$"{leaf}{cell}"];
    }



    public static readonly Dictionary<int, string> UniversalTexts = new()
    {
        {4,  "Ты попадаешь на клетку 4 +1. Я на правах Вселенной, готова дать тебе на выбор: машину, квартиру, дом, бизнес, партнера, путешествие. \r\nТвоя задача написать сюда максимально подробно свое желание, самое главное какие чувства и ощущения ты будешь испытывать, когда оно исполнится. \r\nЯ в течение 24 ч исполню твое желание (проговорю его, усилю своей энергией и погружу тебя в состояние, тчо это у тебя уже есть). И твоя задача из этого состояния, что ты уже имеешь то, что хочешь, понять какие шаги ты можешь сделать уже в течение недели, чтобы стать ближе к своей мечте. \r\nВ дальнейшем, ты можешь прослушивать исполненное желание снова и снова погружаться в состояние, что это уже есть. Такая техника называется «Вспоминая будущее» и она очень круто работает! Ведь наш мозг не видит разницы между реальностью и тем, что ты думаешь, но этого еще нет в жизни. Именно поэтому, исполнение желаний по данной технике создают новые пути возможностей для реализации твоего желания. \r\nПиши свое желание: выбери что-то одно, не волнуйся, в каждом лепестке есть клетки, попадая на которые я на правах Вселенной исполняю желания. \r\n" },
        {6,  "Ты попадаешь на клетку 6 - БОНУС + 500 Единиц счастья и ты можешь выбрать метафорическую карту.\nЕсть колоды: Про деньги и просто карты с разными картинками. Посмотри на выбранную карту, что ты чувствуешь, тебе нравится карта, опиши, что ты на ней видишь. \r\n" },
        {7,  "Ты попадаешь на клетку 7 – ЗАЧЕМ? \r\n\tПодумай над своим запросом, что ты хочешь подробно разобрать? Это может быть абсолютно любой вопрос. \r\n\tСамостоятельно на листочке или в заметках, пиши свой запрос, далее вопрос ЗАЧЕМ? Пишешь ответ на этот вопрос, далее на полученный ответ снова задаешь вопрос ЗАЧЕМ? На ответ снова вопрос ЗАЧЕМ? И так от 5 до 7 раз. \r\n\tТы спросишь для чего? Отвечу, зачастую, нам очень много навязывают родители, друзья, общество и т.д. и данная техника работы над своим запросом помогает тебе понять а что же является твоей истинной целью, которая побуждает тебя! \r\n\tЭту технику ты можешь выполнять в любой непонятной ситуации, она круто работает, но только важно писать то, что сразу пришло тебе в голову, не подвергая эти мысли размышлениям, которые будут говорить тебе: «Да это фигня, зачем тратить на то время и так далее...» \r\n\tРаботаешь самостоятельно с этой техникой, когда будешь готов, бросай кубик." },
        {8,  "Ты попадаешь на клетку 8 – САМОПРЕЗЕНТАЦИЯ. \r\nРасскажи о себе коротко, буквально в течение 1 минуты. Кто ты, чем ты занимаешься, с чем и по какому вопросу к тебе можно обратиться. \r\nМожешь написать текстом, но лучше всего записать видео и прислать сюда в чат, не переживай о том, что ты как-то не так выглядишь, это абсолютно не важно! Важно преодолеть себя и записать краткое видео о себе!  \r\nПочему это важно? Наша реализация и в том числе доход зависят от нашей проявленности, а самопрезентация это самая простая, но в тоже время очень эффективная возможность проявить себя, рассказать о себе и в то же время можно продать свои услуги. Ведь именно благодаря проявленности, самопрезентации мы можем находить себе новые знакомства, друзей, клиентов.\r\nЯ в тебя, верю, ты МОЛОДЕЦ! У тебя все получится! Жду твое видео ;-) " },
        {9,  "Ты попадаешь на клетку 9 – ЗНАК СУДЬБЫ. И я на правах Вселенной, готова исполнить любое твое желание! Чего ты хочешь? \r\nХочу внести ясность относительно тех желаний, которые реально сбываются и я на правах Вселенной их исполняю (озвучиваю): \r\n- желание должно касаться только вас, не про партнера, не про детей, родителей, нет, только про вас! \r\n- в результате исполнения этого желания, вы наполнены счастьем, любовью, радостью и вы прямо ощущаете эти чувства! \r\n- желание должно быть во благо вам и другим людям, другого я не исполню! \r\nИтак, жду твое желание, которое ты максимально подробно опишешь и самое главное, опиши те чувства и ощущения, которые ты будешь испытывать при его исполнении.\r\nВ течение 24 часов пришлю видео с твоим желанием! \r\n" },
        {10, "Ты попадаешь на клетку 10 – КОЛЕСО ФОРТУНЫ + 500 Единиц Счастья и можешь выбрать метафорическую карту, посмотри на выбранную карту, что ты чувствуешь, тебе нравится карта, опиши, что ты на ней видишь. \r\n" },
        {12, "Ты попадаешь на клетку 12 – ИТОГИ. \r\nИтак, пришло время подвести итоги, опиши какая сумма денег у тебя сейчас есть (сколько Единиц Счастья ты получил в игре), какие желания исполнила Вселенная, если ты попадал на такие клетки и поделись своими инсайтами, мыслями, которые ты осознал и принял для себя в ходе игры. \r\nЭто важная клетка, зачастую мы пропускаем этот момент и не видим своих успехов, достижений, что также мешает нам в достижении наших целей. \r\nЕсли тебе эта клетка выпадает повторно, это означает, что это крайне важный для тебя момент, попробуй внедрить в свою жизнь подведение итогов недели, месяца и постановку целей, написание планов на неделею, месяц, год. При внедрении и регулярном использовании этой техники в твою жизнь, ты сам удивишься как изменится твоя жизнь, ты станешь больше успевать, при этом делать это планово, без особых усилий. И обязательно введи пункты с отдыхом, ведь после хорошего отдыха ты будешь гораздо продуктивнее, быстрее и эффективнее делать свои задачи. \r\n" },
        {13, "Ты попадаешь на клетку 13 – ПЕРО ЖАР ПТИЦЫ - +1000 Единиц Счастья и я на правах Вселенной готова исполнить любое твое желание! Чего ты хочешь? \r\n\tХочу внести ясность относительно тех желаний, которые реально сбываются и я на правах Вселенной их исполняю (озвучиваю): \r\n\t- желание должно касаться только вас, не про партнера, не про детей, родителей, нет, только про вас! \r\n\t- в результате исполнения этого желания, вы наполнены счастьем, любовью, радостью и вы прямо ощущаете эти чувства! \r\n\t- желание должно быть во благо вам и другим людям, другого я не исполню! \r\n\tИтак, жду твое желание, которое ты максимально подробно опишешь и самое главное, опиши те чувства и ощущения, которые ты будешь испытывать при его исполнении.\r\n\tВ течение 24 часов пришлю видео с твоим желанием! \r\n" },
    };

